.controls {
  margin-bottom: 1rem;
  font-family: sans-serif;
}

input {
  padding: 0.5rem;
  margin-right: 1rem;
  border: 1px solid #ccc;
  border-radius: 4px;
  font-size: 1rem;
}

button {
  padding: 0.5rem 1rem;
  background: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 1rem;
}

button:hover {
  background: #0056b3;
}

.output {
  padding: 1rem;
  border: 1px solid #ccc;
  border-radius: 4px;
  min-height: 100px;
  font-family: monospace;
  white-space: pre-line;
  line-height: 1.5;
  background: #f8f9fa;
}

solution.js:
const numberInput = document.getElementById('number');
const checkBtn = document.getElementById('checkBtn');
const output = document.getElementById('output');

checkBtn.addEventListener('click', () => {
  const num = Number(numberInput.value);
  let result = '';

  // Check if number is even or odd
  if (num % 2 === 0) {
    result = `${num} is even\n`;
  } else {
    result = `${num} is odd\n`;
  }

  // Use switch to categorize the number
  switch (true) {
    case num < 10:
      result += 'This is a single digit number\n';
      break;
    case num < 100:
      result += 'This is a double digit number\n';
      break;
    default:
      result += 'This is a large number\n';
  }

  // Count from 1 to the number
  result += '\nCounting up:';
  for (let i = 1; i <= num; i++) {
    if (i > 10) {
      result += '\n... (counting continued)';
      break;
    }
    result += `\n${i}`;
  }

  // Count down from number using while loop
  let count = num;
  result += '\n\nCounting down:';
  while (count >= num - 5 && count > 0) {
    result += `\n${count}`;
    count--;
  }

  // Display the result
  output.textContent = result;
  console.log('Analysis complete!');
});
