solution.css:
#output {
  padding: 1rem;
  border: 1px solid #ccc;
  border-radius: 4px;
  font-family: monospace;
}

solution.js:
function add(a, b) {
  return a + b;
}

function multiply(a, b) {
  return a * b;
}

function greet(name) {
  return `Hello, ${name}!`;
}

function isEven(number) {
  return number % 2 === 0;
}
